import React from "react";
// import DataFetch from "./DataFetch";
import BTCFetch from "./BTCFetch"
import BATFetch from "./BATFetch"
import ETHFetch from "./ETHFetch"
import XRPFetch from "./XRPFetch"
import ADAFetch from "./ADAFetch"
// import DisplayTable from "./DisplayTable"
// import data from "./data"


// function updateArray(){

// }

function Button(){

    const [btc,setbtc]=React.useState(true)
    const [eth,seteth]=React.useState(false)
    const [bat,setbat]=React.useState(false)
    const [xrp,setxrp]=React.useState(false)
    const [ada,setada]=React.useState(false)

    function btcClick(){
        setbtc(true);
        setada(false)
        setxrp(false)
        setbat(false)
        seteth(false)
    }

    function ethClick(){
        seteth(true);
        setada(false)
        setxrp(false)
        setbat(false)
        setbtc(false)
    }

    function batClick(){
        setbat(true);
        setada(false)
        setxrp(false)
        setbtc(false)
        seteth(false)
    }
    function xrpClick(){
        setxrp(true);
        setada(false)
        setbtc(false)
        setbat(false)
        seteth(false)
    }
    function adaClick(){
        setada(true);
        setbtc(false)
        setxrp(false)
        setbat(false)
        seteth(false)
    }

    return (
        <div>
        <button type="button" className="btn btn-primary bt" onClick={btcClick}>BTC-USDT</button>
         <button type="button" className="btn btn-primary bt" onClick={ethClick}>ETH-USDT</button>
         <button type="button" className="btn btn-primary bt" onClick={batClick}>BAT-USDT</button>
         <button type="button" className="btn btn-primary bt" onClick={xrpClick}>XRP-USDT</button>
         <button type="button" className="btn btn-primary bt" onClick={adaClick}>ADA-USDT</button>
         {btc?<BTCFetch/>:""}
         {eth?<ETHFetch/>:""}
         {bat?<BATFetch />:""}
         {xrp?<XRPFetch/>:""}
         {ada?<ADAFetch/>:""}
         </div>
    )
}

export default Button
