import React, { useEffect, useState } from "react";
import axios from "axios";
import Trade from "./Trade"


function DataFetch(props){
    const[post,setPost]=React.useState([]);


    const [click,setClick]=useState(false);
    const [text,setText]=useState("");
    const [exchage,setExchange]=useState("");
    const [price,setPrice]=useState(0);
    const [tb, setTb] = useState([]);

   useEffect(()=>{
        axios.get(`/${props.id}`)
        .then(res=>{
            // console.log(res);
            setPost(res.data);
        })
        .catch(err=>{
            console.log(err);
        })
    },[])


    return(
        <div>         
            <table>
                <thead>
                    <tr>
                        <th>Coin</th>
                        <th>Price</th>
                        <th>Exchange</th>
                    </tr>
                </thead>
                <tbody>
                    {post.map(x=>(
                        <tr>
                            <td>{x.id}</td>
                            <td>{x.price}</td>
                            <td>{x.exc}</td>
                            <td>
                            <button className="BuyButton" variant="contained" size="small" color="primary" onClick={()=>{
                                 setClick(true);
                    setText("Buy")
                    setExchange(x.exc)
                    setPrice(x.price)
                            }}> Buy</button>
                            <button className="SellButton" variant="contained" size="small" onClick={()=>{
                                setClick(true);
                    setText("Sell")
                    setExchange(x.exc)
                    setPrice(x.price)
                            }}>Sell</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            {click===true && text.length>0? <Trade action={text} coin={props.id} exchange={exchage} price={price}/>:null}
        </div>
    )
}



setTimeout()

export default DataFetch;
