    const [click,setClick]=useState(false);
    const [text,setText]=useState("");
    const [exchage,setExchange]=useState("");
    const [price,setPrice]=useState(0);
    const [tb, setTb] = useState([]);


   useEffect(()=>{
        axios.get(`/${props.id}`)
        .then(res=>{
            // console.log(res);
            setPost(res.data);
        })
        .catch(err=>{
            console.log(err);
        })
    },[])

    // React.useEffect(()=>{
    //         console.log(price);
