        // //     console.log(cl);
        // // }
    // },[post])

    // React.useEffect(()=>{
    //     console.log("DataFEtc")
    //     localStorage.setItem("coins-data",JSON.stringify(post))
    //     localStorage.setItem("cl",JSON.stringify(click))
    // },[post])

    // the price is not getting updated 
    function func(hell){
        console.log("it has been called")
        console.log("the price is "+hell +" && "+price)
    }

    return(
        <div>         
            <table className="table" style={{marginTop:"1.5rem"}}>
                <thead>
                    <tr>
                        <th>Coin</th>
                        <th>Price</th>
                        <th>Exchange</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {post.map((x,idx)=>(
                        <tr key={idx} className="tablemargin">
                            <td>{x.id}</td>
                            <td>{x.price}</td>
                            <td>{x.exc}</td>
                            <td>

                            <button type="button" className="btn btn-success mari" onClick={()=>{
                                setClick(true);
                                setText("Buy")
                                setExchange(x.exc)
                                setPrice(x.price)
                                setTimeout(setPrice(x.price),1);
                    // console.log(x.price);
                            }}> Buy</button>
                            <button type="button" className="btn btn-danger mari" onClick={()=>{
                                setClick(true);
                                setText("Sell")
                                setExchange(x.exc)
                                setPrice(x.price)
                                func(x.price);
                            }}>Sell</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            {click===true && text.length>0? <Trade action={text} coin={props.id} exchange={exchage} price={price}/>:null}
        </div>
    )
}

function reload(){
    window.location.reload(true);
}

