import * as React from 'react';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';



export default function BasicCard(props) {

    const [val,setVal]=React.useState(0);
    // const [total,setTotal]=React.useState(0);
    var total=0;


    function handleChange(event){
        console.log(event.target);
        setVal(event.target.value)
    }

  return (
    <Card sx={{ minWidth: 275 }} style={{}}>
      <CardContent>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
          Summary of Order
        </Typography>
        <Typography variant="h5" component="div">
          Qty to {props.action}
        </Typography>
        <input type="number" style={{borderRadius:"5%"}} placeholder="qty" value={val} onChange={handleChange} ></input>
        <Typography sx={{ mb: 1.5 }} color="text.secondary">
          {/* adjective */}
        </Typography>

        {val>0?
        <Typography variant="body2">
          you are about to {props.action} {val} {props.coin} from {props.exchange} at a price of {props.price} per coin 
          <br></br>
          Total Amount = {total}
        </Typography>
        :null
        }


      </CardContent>
      <CardActions style={{marginLeft:"auto"}}>
        <Button size="small">Confirm Order</Button>
      </CardActions>
    </Card>
  );
}

