import * as React from 'react';
import Box from '@mui/material/Box';
import axios from 'axios';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';



export default function BasicCard(props) {

    const [val,setVal]=React.useState(0);
    const [total,setTotal]=React.useState(0);
      const url="";

   function submit(event){
       event.preventDefault();
        var date =new Date()
       date = date.getUTCFullYear() + '-' +
       ('00' + (date.getUTCMonth()+1)).slice(-2) + '-' +
       ('00' + date.getUTCDate()).slice(-2) + ' ' + 
       ('00' + date.getUTCHours()).slice(-2) + ':' + 
       ('00' + date.getUTCMinutes()).slice(-2) + ':' + 
       ('00' + date.getUTCSeconds()).slice(-2);

        var obj={
          id:date,
          currency:props.coin,
          exchange_name:props.exchange,
           price:props.price,
          quantity:parseInt(val),
          execution:"sucess"
        }

        console.log(obj);


      //  axios.post("http://localhost:3001/trades",
      //  {
      //   id:date,
      //    quantity:parseInt(val),
      //    currency:props.coin,
      //    exchange_name:props.exchange,
      //    price:props.price,
      //    execution:"sucess"
      // })
      // .then(res=>{
      //   console.log(res.data)
      // })
  }


  return (
    <form onSubmit={(event)=>submit(event)}>
    <Card sx={{ minWidth: 275 }} style={{textAlign:"center"}}>
      <CardContent>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
          Summary of Order
        </Typography>
        <Typography variant="h5" component="div">
          Qty to {props.action}
        </Typography>
        <input type="number" style={{borderRadius:"5%"}} placeholder="qty" value={val} onChange={(event)=>{
          // console.log(event.target.value)
          setVal(event.target.value)
        }} ></input>
        <Typography sx={{ mb: 1.5 }} color="text.secondary">
          {/* adjective */}
        </Typography>

        {val>0?
        <Typography variant="body2">
          you are about to {props.action} {val} {props.coin} from {props.exchange} at a price of {props.price} per coin 
          <br></br>
        </Typography>
        :null
        }
        <Typography>Total amount to be paid = {total}</Typography>


      </CardContent>
      <CardActions style={{marginLeft:"auto"}}>
        <Button size="small" >Confirm Order</Button>
        <button type='submit'>Press here</button>
      </CardActions>
    </Card>
    </form>
  );
}

