import React, { useEffect, useState } from "react";
import axios from "axios";
import Trade from "./Trade"
import BuySellButton from "./BuySellButton.jsx";
// function DataFetching()
// {

// }
const ExchangeRankTable = (props) => {

  const [click,setClick]=useState(false);


  const [text,setText]=useState("");

  const [exchage,setExchange]=useState("");

  const [price,setPrice]=useState(0);

  function buy(){
    setClick(true);
    setText("Buy")
    set
  }

  function sell(){
    setClick(true);
    setText("Sell")
  }

  const [tb, setTb] = useState([]);
  useEffect(() => {
    axios
      .get(`./record${props.id}.json`)
      .then((res) => {
        console.log(res);
        setTb(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [props.id]);
  return (
    <div className="ExchangeRankTable">
      <table>
        <tr>
          <th>Exchange</th>
          <th>Price</th>
          <th>Buy/Sell</th>
        </tr>

        {tb.map((data) => (
          <tr key={data.id}>
            <td>{data.exc}</td>
            <td>{data.price}</td>
            <td>
                <button className="BuyButton" variant="contained" size="small" color="primary" onClick={buy}> Buy</button>
                <button className="SellButton" variant="contained" size="small" onClick={sell} >Sell</button>
            </td>
          </tr>
        ))}
      </table>
      {click===true && text.length>0? <Trade action={text} coin={props.id}/>:null}
    </div>
  );
};
export default ExchangeRankTable;
